mapper.select:
select rownum, sub_att_no as "subAttNo", u_id as "userId", u_name as "userName", sub_attendance_title as "title", sub_att_date as "writeDate", result, sub_status as "subStatus" from( select rownum as rnum, sub_att_no , u_id, u_name, sub_attendance_title, sub_att_date, result, sub_status from( select sub_att_no , u_id, u_name, sub_attendance_title, sub_att_date, result, sub_status from( select sub_att_no, u.u_id, u.u_name, sub_attendance_title, sub_att_date, result, sub_status from users u, sub_attendance s where u.u_id = s.u_id
and result != '미처리'
and result ='미처리'
) order by sub_att_no desc ) where rownum <= #{end} ) where rnum >= #{start}

mapper.update:
UPDATE attendance SET status='휴가'
, leave_time = #{subLeaveTime}
, att_time = #{subAttTime}, leave_time = #{subLeaveTime}
WHERE att_no=#{attNo}

mapper.select:
select u_id
from users
where subject_id = #{subjectId}

select rownum, RP_REPORT_NO AS "rpReportNo", RP_BOARD_ID AS "rpBoardId", RP_CONTENT AS "rpContent", RP_COMMENT_NO AS "rpCommentNo", RP_MASTER_NO AS "rpMasterNo", RP_STATUS AS "rpStatus", RP_TYPE AS "rpType", USER_ID AS "userId", RP_WRITE_DATE AS "rpWriteDate" from( select rownum as rnum, RP_REPORT_NO, RP_BOARD_ID, RP_CONTENT, RP_COMMENT_NO, RP_MASTER_NO, RP_STATUS, RP_TYPE, USER_ID, RP_WRITE_DATE from( select RP_REPORT_NO, RP_BOARD_ID, RP_CONTENT, RP_COMMENT_NO, RP_MASTER_NO, RP_STATUS, RP_TYPE, USER_ID, RP_WRITE_DATE from( select RP_REPORT_NO, RP_BOARD_ID, RP_CONTENT, RP_COMMENT_NO, RP_MASTER_NO, RP_STATUS, RP_TYPE, USER_ID, RP_WRITE_DATE
from reports
and RP_STATUS != '미처리'
and RP_STATUS ='미처리'
) order by RP_WRITE_DATE desc ) where rownum <=#{end} ) where rnum >= #{start}
