mapper.select:

SELECT COUNT(BOARD_ID) AS "count"
FROM BOARD
WHERE CATEGORY_ID = #{categoryId}

SELECT COUNT(BOARD_ID) AS "count"
FROM BOARD
WHERE 
	TITLE LIKE #{keyword}
	OR
	CONTENT LIKE #{keyword}

mapper.update:

UPDATE BOARD
	SET READ_COUNT = READ_COUNT+1
WHERE BOARD_ID=#{boardId}

mapper.select:

SELECT PASSWORD
FROM BOARD
WHERE BOARD_ID=#{boardId}
