mapper.select:

SELECT att_no
FROM attendance
WHERE to_char(att_date, 'YYYY-MM-DD') = #{today}
AND u_id = #{userId}


SELECT att_no
FROM ATTENDANCE
WHERE to_char(att_date, 'YYYYMMDD') = #{today}
AND u_id = #{userId}


SELECT sub_att_no 
FROM sub_attendance 
WHERE att_no = #{attNo}
AND sub_attendance_title = #{subAttendanceTitle}
